INSERT INTO `email_templates` VALUES (1,'Registration Email','Please verify your email','This template is used to send Registration Verification Email, when Configuration->Registration Verification is set to YES','<table bgcolor=\"#F0F0F0\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" height=\"100%\" width=\"100%\">\r\n  <tbody><tr>\r\n    <td style=\"background-color: #ffffff;\" align=\"center\" bgcolor=\"#ffffff\" valign=\"top\"><br>\r\n<table class=\"container\" style=\"width:600px;max-width:600px\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"600px\">\r\n<tbody><tr>\r\n<td height=\"30\"></td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\">[LOGO]</td>\r\n</tr>\r\n<tr>\r\n<td height=\"20\"></td>\r\n</tr>\r\n<tr>\r\n<td><p style=\"text-align:center;margin:0;font-family:Helvetica, Arial, sans-serif;font-size:26px;color:#222222;\">Welcome to [COMPANY]</p></td>\r\n</tr>\r\n<tr>\r\n<td align=\"center\"><img src=\"[SITEURL]/assets/images/line.png\" alt=\"line\" height=\"43\" width=\"251\" style=\"width:250px\"></td>\r\n</tr>\r\n<tr>\r\n<td height=\"30\"></td>\r\n</tr>\r\n<tr>\r\n<td class=\"container-padding content\" style=\"padding-left:24px;padding-right:24px;padding-top:12px;padding-bottom:12px;background-color:#ffffff\" align=\"left\"><br>\r\n  <div class=\"title\" style=\"font-family:Helvetica, Arial, sans-serif;font-size:18px;font-weight:600;color:#374550\">Congratulations!</div>\r\n  <br>\r\n  <div class=\"body-text\" style=\"font-family:Helvetica, Arial, sans-serif;font-size:14px;line-height:20px;text-align:left;color:#222222\"> You are now registered member<br>\r\n    <br>\r\n    The administrator of this site has requested all new accounts to be activated by the users who created them thus your account is currently inactive. To activate your account, please visit the link below. <br>\r\n    <br>\r\n  </div></td>\r\n</tr>\r\n<tr>\r\n<td class=\"container-padding content\" style=\"padding-left:24px;padding-right:24px;padding-top:12px;padding-bottom:12